SELECT count(*)
  FROM place_spot_image

		and file_id=#{fileId}	
		and ts_id=#{tsId}	
		and service_type=#{serviceType}	
		and upload_path=#{uploadPath}	
		and upload_filename=#{uploadFilename}	
		and file_extension=#{fileExtension}	
		and file_size=#{fileSize}	
		and file_mimetype=#{fileMimetype}	
		and origin_filename=#{originFilename}	
		and upload_id=#{uploadId}	
		and upload_date=#{uploadDate}

	SELECT * FROM (
		SELECT @rownum:=@rownum+1 AS rownum,
		       file_id, ts_id, service_type, upload_path, upload_filename, file_extension, file_size, file_mimetype, origin_filename, upload_id, upload_date, sort_order
		  FROM place_spot_image
		  join (SELECT @rownum:= 0) rnum

				and file_id=#{fileId}	
				and ts_id=#{tsId}	
				and service_type=#{serviceType}	
				and upload_path=#{uploadPath}	
				and upload_filename=#{uploadFilename}	
				and file_extension=#{fileExtension}	
				and file_size=#{fileSize}	
				and file_mimetype=#{fileMimetype}	
				and origin_filename=#{originFilename}	
				and upload_id=#{uploadId}	
				and upload_date=#{uploadDate}	

		ORDER BY sort_order asc) a

         LIMIT #{itemStartPosition}, #{pagePerSize}

SELECT file_id, ts_id, service_type, upload_path, upload_filename, file_extension, file_size, file_mimetype, origin_filename, upload_id, upload_date
  FROM place_spot_image

		and file_id=#{fileId}	
		and ts_id=#{tsId}	
		and service_type=#{serviceType}	
		and upload_path=#{uploadPath}	
		and upload_filename=#{uploadFilename}	
		and file_extension=#{fileExtension}	
		and file_size=#{fileSize}	
		and file_mimetype=#{fileMimetype}	
		and origin_filename=#{originFilename}	
		and upload_id=#{uploadId}	
		and upload_date=#{uploadDate}

mapper.update:
Update place_spot_image
service_type=#{serviceType}, upload_path=#{uploadPath}, upload_filename=#{uploadFilename}, file_extension=#{fileExtension}, file_size=#{fileSize}, file_mimetype=#{fileMimetype}, origin_filename=#{originFilename}, upload_id=#{uploadId}, sort_order=#{sortOrder}, upload_date=now()
and file_id=#{fileId} and ts_id=#{tsId}

mapper.delete:
        delete from place_spot_image

        		and file_id=#{fileId}	
				and ts_id=#{tsId}
